sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
	var now  = new GlideDateTime();

	var records = new GlideRecord('x_58872_needit_needit_task');
	records.addQuery('due_date','<=',now);
	records.addQuery('assigned_to',current.assigned_to);
	records.addQuery('state','<',3);
	records.query();
	var count = records.getRowCount();
	if(count >1)
		template.print('also yu have '+(count- 1)+' similar records');
	email.addAddress('cc',current.assigned_to.manager);

